mapper.select:
select count(*)
from tbl_cart
where CART_STATUS = 0 and FK_USERNO = #{userno} and FK_CLASS_SEQ = #{class_seq}

select J.cart_seq, J.fk_class_seq, C.class_photo, C.class_title, C.class_semester, C.class_time, C.class_fee, T.teacher_name, C.class_day ,to_char(C.class_startDate, 'yyyy.mm.dd') as class_startDate, to_char(C.class_endDate, 'yyyy.mm.dd') as class_endDate, C.class_status, C.class_personnel, c.class_startdate || '~' || c.class_enddate as class_term from tbl_cart J JOIN tbl_class C on J.fk_class_seq = C.class_seq JOIN tbl_teacher T on C.fk_teacher_seq = T.teacher_seq where cart_seq in
${ deleteLecture[i] }
(#{ cart_seq })
and fk_USERNO = #{ userno }

select count(*) as count
from tbl_student
where fk_class_seq = #{class_seq}

select count(*)
from awesome_orderlist
where userno_fk = #{userno} and class_seq_fk = #{class_seq}
